import styled from 'styled-components'
import { Link as LinkR } from 'react-router-dom'
import { Link as LinkS } from 'react-scroll'
import Popup from 'reactjs-popup';
export const Nav = styled.nav`
        background: ${({ scrollNav }) => (scrollNav ? '#000' : 'transparent')};
        height: 80px;
        display: flex;
        margin-top: -80px
        justify-center: center;
        align-items: center;
        font-size: 1rem;
        position: sticky;
        top:0;
        z-index:10;
        transition: 0.5s all ease;
        @media screen and (max-width: 960px){
            transition: 0.8s all ease;
        }
    `;

export const NavbarContainer = styled.div`
        display: flex;
        justify-content: space-between;
        height: 80px;
        z-index: 1;
        width: 100%;
        padding: 0 24px;
    `;

export const NavLogo = styled(LinkR)`
        color: #fff;
        justify-self: flex-start;
        cursor: pointer;
        font-size: 1.5rem;
        display:flex;
        align-items: center;
        margin-left: 24px;
        font-weight: bold;
        text-decoration: none;
    `;

export const MobileIcon = styled.div`
        display: none;

        @media screen and (max-width: 768px){
            display: block;
            position: absolute;
            top: 0;
            right: 0;
            transform: translate(-100%, 70%);
            font-size: 1.8rem;
            cursor: pointer;
            color: #fff;
        }
    `;
export const NavMenu = styled.ul`
        display: flex;
        align-items: center;
        list-style: none;
        text-align: center;
        margin-right: -22px;

        @media screen and (max-width: 768px){
            display:none;
        }
    `;

export const NavItems = styled.li`
        height: 80px;
    `;
export const NavLinks = styled(LinkS)`
        color: #fff;
        display: flex;
        align-items: center;
        text-decoration: none;
        padding: 0 1rem;
        height: 100%;
        cursor: pointer;

        &.active {
            border-bottom: 4px solid #01bf71;
        }
        &:hover {
            border-bottom: 4px solid #01bf71;
        }
    `;
export const NavButton = styled.nav`
    display: flex;
    align-items: center;
        @media screen and (max-width: 768px){
            display:none;
        }
    `;
export const NavBtnLink = styled(LinkR)`
        border-radius: 50px;
        background: #01bf71;
        white-space: nowrap;
        padding: 10px 22px;
        color: #010606;
        font-size: 16px;
        outline: none;
        border: none;
        cursor: pointer;
        transition: all 0.2s ease-in-out;
        text-decoration: none;

        &:hover {
            transition: all 0.2s ease-in-out;
            background: #fff;
            color: #010606;

        }
    `;

export const StyledPopup = styled(Popup)`
  &-arrow {
      display: none;
  }
  &-content {
      align-items: center;
      align-text: center;
    justify-content: center;
    transform: translate(0%, 35%);
}
 &-overlay{
    background: rgba(0, 0, 0, 0.5);
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
 }
@media screen and (max-width: 768px){
    display:none;
}
`;


export const NavBtn = styled.div`
        border-radius: 50px;
        background: #01bf71;
        white-space: nowrap;
        padding: 10px 8px;
        color: #010606;
        font-size: 16px;
        outline: none;
        border: none;
        cursor: pointer;
        transition: all 0.2s ease-in-out;
        text-decoration: none;
        &:hover {
            transition: all 0.2s ease-in-out;
            background: #fff;
            color: #010606;

        }
    `;

export const NavbarContactMe = styled.div`
    border-radius: 50px;
    background: #01bf71;
    white-space: nowwrap;
    padding: 16px 25px;
    color: #fff;
    font-size: 1rem;
    outline: none;
    border: none;
    transition: all 0.2s ease-in-out;
    text-decoration: none;
    font-family: 'Open Sans', sans serif;

`
export const NavOutLink = styled(LinkR)`
    text-decoration: none;
`
